å‚è€ƒ  jsonimport pandas as pdimport torchfrom datasets import Datasetfrom modelscope import snapshot_download, AutoTokenizerfrom swanlab.integration.huggingface import SwanLabCallbackfrom peft import LoraConfig, TaskType, get_peft_modelfrom transformers import AutoModelForCausalLM, TrainingArguments, Trainer, DataCollatorForSeq2Seqimport osimport swanlabdef dataset_jsonl_transfer(origin_path, new_path): """ å°†åŸå§‹æ•°æ®é›†è½¬æ¢ä¸ºå¤§æ¨¡å‹å¾®è°ƒæ‰€éœ€æ•°æ®æ ¼å¼çš„æ–°æ•°æ®é›† """ messages = [] # è¯»å–æ—§çš„JSONLæ–‡ä»¶ with open(origin_path, "r") as file: for line in file: # è§£ææ¯ä¸€è¡Œçš„jsonæ•°æ® data = json.loads(line) input_text = data["text"] entities = data["entities"] match_names = ["åœ°ç‚¹", "äººå", "åœ°ç†å®ä½“", "ç»„ç»‡"] entity_sentence = "" for entity in entities: entity_json = dict(entity) entity_text = entity_json["entity_text"] entity_names = entity_json["entity_names"] for name in entity_names: if name in match_names: entity_label = name break entity_sentence += f"""&#123;"entity_text": "&#123;entity_text&#125;", "entity_label": "&#123;entity_label&#125;"&#125;""" if entity_sentence == "": entity_sentence = "æ²¡æœ‰æ‰¾åˆ°ä»»ä½•å®ä½“" message = &#123; "instruction": """ä½ æ˜¯ä¸€ä¸ªæ–‡æœ¬å®ä½“è¯†åˆ«é¢†åŸŸçš„ä¸“å®¶ï¼Œä½ éœ€è¦ä»ç»™å®šçš„å¥å­ä¸­æå– åœ°ç‚¹; äººå; åœ°ç†å®ä½“; ç»„ç»‡ å®ä½“. ä»¥ json æ ¼å¼è¾“å‡º, å¦‚ &#123;"entity_text": "å—äº¬", "entity_label": "åœ°ç†å®ä½“"&#125; æ³¨æ„: 1. è¾“å‡ºçš„æ¯ä¸€è¡Œéƒ½å¿…é¡»æ˜¯æ­£ç¡®çš„ json å­—ç¬¦ä¸². 2. æ‰¾ä¸åˆ°ä»»ä½•å®ä½“æ—¶, è¾“å‡º"æ²¡æœ‰æ‰¾åˆ°ä»»ä½•å®ä½“". """, "input": f"æ–‡æœ¬:&#123;input_text&#125;", "output": entity_sentence, &#125; messages.append(message) # ä¿å­˜é‡æ„åçš„JSONLæ–‡ä»¶ with open(new_path, "w", encoding="utf-8") as file: for message in messages: file.write(json.dumps(message, ensure_ascii=False) + "
")def process_func(example): """ å°†æ•°æ®é›†è¿›è¡Œé¢„å¤„ç† """ MAX_LENGTH = 384 input_ids, attention_mask, labels = [], [], [] system_prompt = """ä½ æ˜¯ä¸€ä¸ªæ–‡æœ¬å®ä½“è¯†åˆ«é¢†åŸŸçš„ä¸“å®¶ï¼Œä½ éœ€è¦ä»ç»™å®šçš„å¥å­ä¸­æå– åœ°ç‚¹; äººå; åœ°ç†å®ä½“; ç»„ç»‡ å®ä½“. ä»¥ json æ ¼å¼è¾“å‡º, å¦‚ &#123;"entity_text": "å—äº¬", "entity_label": "åœ°ç†å®ä½“"&#125; æ³¨æ„: 1. è¾“å‡ºçš„æ¯ä¸€è¡Œéƒ½å¿…é¡»æ˜¯æ­£ç¡®çš„ json å­—ç¬¦ä¸². 2. æ‰¾ä¸åˆ°ä»»ä½•å®ä½“æ—¶, è¾“å‡º"æ²¡æœ‰æ‰¾åˆ°ä»»ä½•å®ä½“".""" instruction = tokenizer( f"&lt;|im_start|&gt;system
&#123;system_prompt&#125;&lt;|im_end|&gt;
&lt;|im_start|&gt;user
&#123; ['input']&#125;&lt;|im_end|&gt;
&lt;|im_start|&gt;assistant
", add_special_tokens=False, ) response = tokenizer(f"&#123;example['output']&#125;", add_special_tokens=False) input_ids = instruction["input_ids"] + response["input_ids"] + [tokenizer.pad_token_id] attention_mask = ( instruction["attention_mask"] + response["attention_mask"] + [1] ) labels = [-100] * len(instruction["input_ids"]) + response["input_ids"] + [tokenizer.pad_token_id] if len(input_ids) &gt; MAX_LENGTH: # åšä¸€ä¸ªæˆªæ–­ input_ids = input_ids[:MAX_LENGTH] attention_mask = attention_mask[:MAX_LENGTH] labels = labels[:MAX_LENGTH] return &#123;"input_ids": input_ids, "attention_mask": attention_mask, "labels": labels&#125;def predict(messages, model, tokenizer): device = "cuda" text = tokenizer.apply_chat_template( messages, tokenize=False, add_generation_prompt=True ) model_inputs = tokenizer([text], return_tensors="pt").to(device) generated_ids = model.generate( model_inputs.input_ids, max_new_tokens=512 ) generated_ids = [ output_ids[len(input_ids):] for input_ids, output_ids in zip(model_inputs.input_ids, generated_ids) ] response = tokenizer.batch_decode(generated_ids, skip_special_tokens=True)[0] print(response) return responsemodel_id = "qwen/Qwen2-1.5B-Instruct"model_dir = "./qwen/Qwen2-1___5B-Instruct"# åœ¨modelscopeä¸Šä¸‹è½½Qwenæ¨¡å‹åˆ°æœ¬åœ°ç›®å½•ä¸‹model_dir = snapshot_download(model_id, cache_dir="./", revision="master")# TransformersåŠ è½½æ¨¡å‹æƒé‡tokenizer = AutoTokenizer.from_pretrained(model_dir, use_fast=False, trust_remote_code=True)model = AutoModelForCausalLM.from_pretrained(model_dir, device_map="auto", torch_dtype=torch.bfloat16)model.enable_input_require_grads() # å¼€å¯æ¢¯åº¦æ£€æŸ¥ç‚¹æ—¶ï¼Œè¦æ‰§è¡Œè¯¥æ–¹æ³•# åŠ è½½ã€å¤„ç†æ•°æ®é›†å’Œæµ‹è¯•é›†train_dataset_path = "ccfbdci.jsonl"train_jsonl_new_path = "ccf_train.jsonl"if not os.path.exists(train_jsonl_new_path): dataset_jsonl_transfer(train_dataset_path, train_jsonl_new_path)# å¾—åˆ°è®­ç»ƒé›†total_df = pd.read_json(train_jsonl_new_path, lines=True)train_df = total_df[int(len(total_df) *